import React, { Component } from 'react';
import data from "./data"; 
import Heading from "./Heading"
import "../stylesheets/Display.css"

const teamsList = data.teams;
var archivedTeamsList = [];
var favoritedTeamsList = [];

for (var i = 0; i < teamsList.length; i++) {
    if (teamsList[i].is_archived === true) {
        // add to archived teams list
        archivedTeamsList[archivedTeamsList.length] = teamsList[i].is_archived;
    }

    if (teamsList[i].is_favorited === true) {
        // add to favorites list
        favoritedTeamsList[favoritedTeamsList.length] = teamsList[i].is_favorited;
    }
}

function length(obj) {
    return Object.keys(obj).length;
}

class Display extends Component {
    constructor(props) {
        super(props);
        this.state = {
            tab: "", // displayed depends on the tag?
            displayedTeams: teamsList, // implement getTeams function; change to []
        };
    }

    getTeams(tab) {
        var response;

        if (tab === "All") {
            response = teamsList;
        } else if (tab === "Archived") {
            response = archivedTeamsList;
        } else if (tab === "Favorited") {
            response = favoritedTeamsList;
        } else {
            console.error("Invalid tab");
        }

        this.setState({displayedTeams: response});
    }

    getTab() {

    }

    componentDidMount() {
        this.getTab();
        this.getTeams(this.state.tab);
    }

	render() {
		return (
            <div className="teams">
                <div class="wrapper border-bottom">
                    <div class="inner">
                    <span id="title">
                        All Teams
                    </span>
                    <span id="showing">
                        Showing {length(teamsList)} out of {length(teamsList)} teams
                    </span>
                    </div>
                </div>

                <div class="container border">
                    <ul>{teamsList.map(t => (<li key={t.id} >{t.name}</li>))}</ul>
                </div>
            </div>
        );
    }
} 
export default Display;
