import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons'
import { faSearch } from '@fortawesome/free-solid-svg-icons'

import data from "./data"; 
import "../stylesheets/Heading.css"

function changeColor() {
    var icon = document.getElementsByTagName("svg");
    icon.style = {color: "blue"};
}

class Heading extends Component {
    componentWillMount() {
        var searchBox = document.getElementsByTagName("input");
        searchBox.addEventListener("focus", changeColor);
        searchBox.addEventListener("hover", changeColor);
    }

    render() {
        return (
            <div class="title">
                <img src="https://i.ibb.co/Jv7WkyL/teams-grey.png" alt="teams-grey" border="0"></img>
                <span class="title-words">Teams</span>
                <button class="create">
                    <FontAwesomeIcon icon={faPlus} style={{marginRight: "13px"}}/>
                    CREATE NEW TEAM</button>

                <div class="col">
                    <div class="topnav">
                    <FontAwesomeIcon icon={faSearch} style={{}}/>
                    <input type="text" placeholder="Search team name ..." 
                        onFocus={(e) => e.target.placeholder = "Enter team name ..."}
                        onBlur={(e) => e.target.placeholder = "Search team name ..."} />
                    <span class="underline"></span>
                    </div>
                </div>
            </div>
        );
    }
}

export default Heading;
